mapper.insert:
insert into tbl_clue
(id, fullname, appellation,
  owner, company, job, email,
  phone, website, mphone,
  state, source, create_by,
  create_time, edit_by, edit_time,
  description, contact_summary, next_contact_time,
  address)
  values (#{id},#{ fullname},#{ appellation},#{
  owner},#{ company},#{ job},#{ email},#{
  phone},#{ website},#{ mphone},#{
  state},#{ source},#{ createBy},#{
  createTime},#{ editBy},#{ editTime},#{
  description},#{ contactSummary},#{ nextContactTime},#{
  address})

mapper.select:
select tc.id,tc.fullname,tdv.value as appellation,u1.name as owner,tc.company,tc.job,tc.email,tc.phone,tc.website,tc.mphone,tdv2.value as state,tdv3.value as source, u.name as create_by,tc.create_time,u2.name as edit_by,tc.edit_time,tc.description,tc.contact_summary,tc.next_contact_time,tc.address from tbl_clue tc left join tbl_dic_value tdv on tdv.id=tc.appellation left join tbl_dic_value tdv2 on tdv2.id=tc.state left join tbl_dic_value tdv3 on tdv3.id=tc.source join tbl_user u1 on u1.id=tc.owner join tbl_user u on u.id=tc.create_by left join tbl_user u2 on u2.id=tc.edit_by
and tc.fullname like '%' #{fullname} '%'
and tc.company like '%' #{company} '%'
and tc.phone like '%' #{phone} '%'
and tdv3.id=#{source}
and u1.name like '%' #{owner} '%'
and tc.mphone like '%' #{mphone} '%'
and tdv2.id=#{state}
order by tc.create_time desc limit #{beginNo},#{pageSize}

mapper.delete:
 delete from tbl_clue
where id in

  #{id}
